'use strict';

/**
 * Vertigo:
 *
 * @constructor
 * @param {Object} options Vertigo's configuration.
 * @api public
 */
function Vertigo(options) {
  if (!this) return new Vertigo(options);
}

Vertigo.prototype.__proto__ = require('eventemitter3');

require('supply').middleware(Vertigo);
require('supply').plugin(Vertigo);

Vertigo.prototype.transport = function transport(name, transports) {};
Vertigo.prototype.transform = function transform(type, event, fn) {
  if ('function' === typeof event) {
    fn = event; event = null;
  }
};

//
// Expose the Vertigo instance.
//
module.exports = Vertigo;
